'use strict';

// MODULES //

var debug = require( 'debug' )( 'annotations:main' );
var EventEmitter = require( 'events' ).EventEmitter;
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// ANNOTATIONS //

/**
* Annotations constructor.
*
* @constructor
* @returns {Annotations} annotations instance
*
* @example
* var annotations = new Annotations();
*/
function Annotations() {
	var self;
	if ( !( this instanceof Annotations ) ) {
		return new Annotations();
	}
	self = this;
	debug( 'Creating an instance...' );
	EventEmitter.call( this );
	this.on( 'change', onChange );
	this.on( '_render', onRender );
	return this;

	/**
	* Callback invoked upon receiving a change event.
	*
	* @private
	*/
	function onChange() {
		debug( 'Received a change event.' );
		self.render();
	}

	/**
	* Re-emits a render event.
	*
	* @private
	*/
	function onRender() {
		var args;
		var i;
		debug( 'Received a render event. Re-emitting...' );
		args = new Array( arguments.length+1 );
		args[ 0 ] = 'render';
		for ( i = 0; i < arguments.length; i++ ) {
			args[ i+1 ] = arguments[ i ];
		}
		self.emit.apply( self, args );
	}
} // end FUNCTION Annotations()

/*
* Create a prototype which inherits from the parent prototype.
*/
Annotations.prototype = Object.create( EventEmitter.prototype );

/*
* Set the constructor.
*/
Annotations.prototype.constructor = Annotations;

/**
* Renders a virtual DOM tree.
*
* @memberof Annotations.prototype
* @name render
* @type {Function}
* @returns {VTree} virtual tree
*
* @example
* var annotations = new Annotations();
*
* var out = annotations.render();
*/
setReadOnly( Annotations.prototype, 'render', require( './methods/render.js' ) );


// EXPORTS //

module.exports = Annotations;
