'use strict';

/**
* Returns the tick direction.
*
* @private
* @returns {number} tick direction
*/
function get() {
	/* eslint-disable no-invalid-this */
	if (
		this._orientation === 'top' ||
		this._orientation === 'left'
	) {
		return -1;
	}
	return 1;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
