'use strict';

// MODULES //

var center = require( './center.js' );


// MAIN //

/**
* Returns a function for positioning ticks.
*
* @private
* @returns {Function} position function
*/
function get() {
	/* eslint-disable no-invalid-this */
	var scale = this._scale.copy();
	if ( scale.bandwidth ) {
		return center( scale );
	}
	return scale;
}


// EXPORTS //

module.exports = get;
