'use strict';

/**
* Returns the label `y` coordinate.
*
* @private
* @param {string} orient - axis orientation
* @returns {number} `y` coordinate
*/
function labelYPos( orient ) {
	if ( orient === 'left' ) {
		return -72;
	}
	if ( orient === 'right' ) {
		return 72;
	}
	if ( orient === 'bottom' ) {
		return 45;
	}
	// orient === 'top'
	return -45;
} // end FUNCTION labelYPos()


// EXPORTS //

module.exports = labelYPos;
