'use strict';

/**
* Returns the text anchor value for text positioning.
*
* @private
* @param {string} orient - axis orientation
* @returns {string} text anchor value
*/
function textAnchor( orient ) {
	if ( orient === 'left' ) {
		return 'end';
	}
	if ( orient === 'right' ) {
		return 'start';
	}
	return 'middle';
} // end FUNCTION textAnchor()


// EXPORTS //

module.exports = textAnchor;
