'use strict';

/**
* Returns the "y" attribute for tick positioning.
*
* @private
* @param {string} orient - axis orientation
* @returns {string} attribute
*/
function yAttr( orient ) {
	if ( orient === 'left' || orient === 'right' ) {
		return 'y';
	}
	return 'x';
} // end FUNCTION yAttr()


// EXPORTS //

module.exports = yAttr;
