'use strict';

// MODULES //

var EVENTS = require( './events.json' );


// MAIN //

/**
* Provided a property, returns a corresponding event name for when a property value changes.
*
* @private
* @param {string} prop - property
* @returns {string} event name
*/
function get( prop ) {
	return EVENTS[ prop ];
}


// EXPORTS //

module.exports = get;
