'use strict';

// MODULES //

var debug = require( 'debug' )( 'path:set:color' );
var events = require( './../../events' );
var isValid = require( './../../validators/color.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'color' );


// SET //

/**
* Sets the path color.
*
* @private
* @param {string} color - color
* @throws {TypeError} must be a string primitive
*/
function set( color ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( color );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %d.', this._color );

	this._color = color;
	debug( 'New Value: %d.', this._color );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
