'use strict';

// MODULES //

var isArrayLike = require( '@stdlib/utils/is-array-like' );


/**
* Validates `x`.
*
* @private
* @param {*} x - value to test
* @returns {(Error|null)} error object or null
*/
function test( x ) {
	if ( !isArrayLike( x ) ) {
		return new TypeError( 'invalid value. `x` must be array-like. Value: `' + x + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
