'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// GET //

/**
* Returns a function to get a color.
*
* @private
* @returns {Function} color accessor
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	var self = this;
	if ( isString( this._color ) ) {
		return color;
	}
	return this._color;

	function color() {
		return self._color;
	}
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
