'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// MAIN //

/**
* Returns a function to get a label.
*
* @private
* @returns {Function} label accessor
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	var self = this;
	if ( isString( this._label ) ) {
		return label;
	}
	return this._label;

	function label() {
		return self._label;
	}
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
