'use strict';

// MODULES //

var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;


// GET //

/**
* Returns a function to get an opacity.
*
* @private
* @returns {Function} opacity accessor
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	var self = this;
	if ( isNumber( this._opacity ) ) {
		return opacity;
	}
	return this._opacity;

	function opacity() {
		return self._opacity;
	}
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
