'use strict';

/**
* Returns the tick direction.
*
* @private
* @param {string} orient - orientation
* @returns {number} tick direction
*/
function tickDir( orient ) {
	if (
		orient === 'bottom' ||
		orient === 'right'
	) {
		return -1;
	}
	return 1;
} // end FUNCTION tickDir()


// EXPORTS //

module.exports = tickDir;
