'use strict';

/**
* Returns the "x" attribute for tick positioning.
*
* @private
* @param {string} orient - rug orientation
* @returns {string} attribute
*/
function xAttr( orient ) {
	if ( orient === 'left' || orient === 'right' ) {
		return 'x';
	}
	return 'y';
} // end FUNCTION xAttr()


// EXPORTS //

module.exports = xAttr;
