'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;


// MAIN //

/**
* Returns a function to get a symbol's label.
*
* @private
* @returns {Function} label accessor
*/
function get() {
	/* eslint-disable no-invalid-this */
	var self = this;
	if ( isString( this._label ) ) {
		return label;
	}
	return this._label;

	function label() {
		return self._label;
	}
}


// EXPORTS //

module.exports = get;
