'use strict';

// MODULES //

var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;


// MAIN //

/**
* Returns a function to get a symbol's size.
*
* @private
* @returns {Function} size accessor
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	var self = this;
	if ( isNumber( this._size ) ) {
		return size;
	}
	return this._size;

	function size() {
		return self._size;
	}
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
