'use strict';

// MODULES //

var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;


// MAIN //

/**
* Returns a function to get a symbol's size.
*
* @private
* @returns {Function} size accessor
*/
function get() {
	/* eslint-disable no-invalid-this */
	var self = this;
	if ( isNumber( this._size ) ) {
		return size;
	}
	return this._size;

	function size() {
		return self._size;
	}
}


// EXPORTS //

module.exports = get;
