'use strict';

// MODULES //

var debug = require( 'debug' )( 'symbols:set:symbol' );
var events = require( './../../events' );
var isValid = require( './../../validators/symbol.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'symbol' );


// SET //

/**
* Sets the symbol.
*
* @private
* @param {string} symbol - symbol
* @throws {TypeError} must be a supported symbol
*/
function set( symbol ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( symbol );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %d.', this._symbol );

	this._symbol = symbol;
	debug( 'New Value: %d.', this._symbol );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
