'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isFunction = require( '@stdlib/utils/is-function' );


// TEST //


/**
* Validates `color`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if (
		!isString( v ) &&
		!isFunction( v )
	) {
		return new TypeError( 'invalid value. `color` must be a string primitive or a function. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
