'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isPrimitive;
var isFunction = require( '@stdlib/utils/is-function' );


// TEST //


/**
* Validates `size`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if (
		!isNonNegativeInteger( v ) &&
		!isFunction( v )
	) {
		return new TypeError( 'invalid value. `size` must be a nonnegative integer or a function. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
