'use strict';

// MODULES //

var indexOf = require( '@stdlib/utils/indexof' );
var SYMBOLS = require( './../etc/symbols.json' );


// TEST //


/**
* Validates `symbol`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if ( indexOf( SYMBOLS, v ) === -1 ) {
		return new TypeError( 'invalid value. `symbol` must be a supported symbol. Symbols: ['+SYMBOLS.join(',')+']. Value: `'+v+'`.' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
