var isArrayLike = require( '@stdlib/utils/is-array-like' );


/**
* Validates `y`.
*
* @private
* @param {*} y - value to test
* @returns {(Error|null)} error object or null
*/
function test( y ) {
	if ( !isArrayLike( y ) ) {
		return new TypeError( 'invalid value. `y` must be array-like. Value: `' + y + '.`' );
	}
	return null;
} // end FUNCTION test()


