'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;


/**
* Validates `text`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if ( !isString( v ) ) {
		return new TypeError( 'invalid value. `text` must be a string primitive. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
