'use strict';

var toHTML = require( 'vdom-to-html' );
var randn = require( '@stdlib/math/base/random/box-muller' );
var now = require( '@stdlib/time/now' );
var Plot = require( './../lib' );

var t;
var x;
var y;
var i;

// Create some data...
t = now() * 1000;
x = new Float64Array( 100 );
y = new Float64Array( x.length );
for ( i = 0; i < x.length; i++ ) {
	x[ i ] = t + (i*360000);
	y[ i ] = 50.0 + (10.0*randn());
}

// Create a new plot:
var plot = new Plot( [x], [y], {
	'width': 600,
	'height': 480,
	'xScale': 'time',
	'xTickFormat': '%H:%M'
});

// Render as a virtual DOM tree:
var vtree = plot.render();
console.log( JSON.stringify( vtree ) );

// Transform the virtual DOM tree to HTML:
var html = toHTML( vtree );
console.log( html );

// Listen for 'render' events (e.g., when triggered due to changes in state):
plot.on( 'render', onRender );

setTimeout( update, 1000 );

function update() {
	plot.width = 720;
}

function onRender( vtree ) {
	console.log( toHTML( vtree ) );
}
