'use strict';

// MODULES //

var logger = require( 'debug' );


// VARIABLES //

var debug = logger( 'plot:xpos' );


// MAIN //

/**
* Returns a function to map values to x-axis coordinate values.
*
* @private
* @returns {Function} map function
*/
function get() {
	/* eslint-disable no-invalid-this */
	var scale = this.xScale;
	return xPos;

	/**
	* Maps a value to a x-axis coordinate value.
	*
	* @private
	* @param {number} d - datum
	* @returns {number} pixel value
	*/
	function xPos( d ) {
		var px = scale( d );
		debug( 'Value: %d => Pixel: %d.', d, px );
		return px;
	}// end FUNCTION xPos()
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
