'use strict';

// MODULES //

var h = require( 'virtual-dom/h' );


// BACKGROUND //

/**
* Returns a plot background as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function bkgd( ctx ) {
	var opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'className': 'background',
		'attributes': {
			'x': 0,
			'y': 0,
			'width': ctx.graphWidth(),
			'height': ctx.graphHeight()
		}
	};

	return h( 'rect', opts, [] );
} // end FUNCTION bkgd()


// EXPORTS //

module.exports = bkgd;
