'use strict';

// MODULES //

var h = require( 'virtual-dom/h' );


// FUNCTIONS //

/**
* Returns a clip path rectangle as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function rect( ctx ) {
	var opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'className': 'clipPath',
		'attributes': {
			'width': ctx.graphWidth(),
			'height': ctx.graphHeight()
		}
	};

	return h( 'rect', opts, [] );
} // end FUNCTION rect()


// CLIP PATH //

/**
* Returns a clip path as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function clipPath( ctx ) {
	var children;
	var opts;

	opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'id': ctx._clipPathID
	};

	children = [
		rect( ctx )
	];

	return h( 'clipPath', opts, children );
} // end FUNCTION clipPath()


// EXPORTS //

module.exports = clipPath;
