'use strict';

// MODULES //

var h = require( 'virtual-dom/h' );


// TITLE //

/**
* Returns a plot title as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function plotTitle( ctx ) {
	var opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'plot.title',
		'className': 'title noselect',
		'attributes': {
			'x': 0,
			'y': 0
		}
	};

	return h( 'text', opts, [ ctx.state.title ] );
} // end FUNCTION plotTitle()


// EXPORTS //

module.exports = plotTitle;
