'use strict';

/**
* Computes the expected graph width.
*
* @private
* @returns {number} graph width
*/
function graphWidth() {
	/* jshint validthis: true */ // TODO: eslint
	return this.state.width - this.state.paddingLeft - this.state.paddingRight;
} // end FUNCTION graphWidth


// EXPORTS //

module.exports = graphWidth;
