'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series-engine:methods:triangle' );


// TRIANGLE //

/**
* Creates a triangle using an SVG path.
*
* @private
* @param {Array} d - datum
* @param {number} i - datum index
* @returns {string} SVG path string
*/
function triangle( d ) {
	/* jshint validthis: true */ // TODO eslint
	var p1;
	var p2;
	var p3;
	var x;
	var l;

	x = this.xPos( d );
	debug( 'x (px): %d.', x );

	p1 = (x-4) + ',-9';
	p2 = x + ',-2';
	p3 = (x+4) + ',-9';
	l = 'M' + p1 + 'L' + p2 + 'L' + p3 + 'Z';
	debug( 'Path: %s.', l );

	return l;
} // end FUNCTION triangle()


// EXPORTS //

module.exports = triangle;
