'use strict';

// MODULES //

var isNull = require( '@stdlib/utils/is-null' );
var getMax = require( './../utils/max.js' );


// X-AXIS MAX //

/**
* Computes the maximum value of the x-axis domain.
*
* @private
* @returns {number} maximum value
*/
function xMax() {
	/* jshint validthis: true */ // TODO: eslint
	var max;
	if ( isNull( this.state.xMax ) ) {
		max = getMax( this.state.x );
		return ( isNull( max ) ) ? 1.0 : max;
	}
	return this.state.xMax;
} // end FUNCTION xMax()


// EXPORTS //

module.exports = xMax;
