'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:set:numticks' );
var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isPrimitive;
var events = require( './../../events' );


// VARIABLES //

var CHANGE_EVENT = events( 'numTicks' );


// SET //

/**
* Sets the number of axis ticks.
*
* @private
* @param {NonNegativeInteger} num - num
* @throws {TypeError} must be a nonnegative integer
*/
function set( num ) {
	/*jshint validthis: true */ // TODO: eslint
	if ( !isNonNegativeInteger( num ) ) {
		throw new TypeError( 'invalid value. `numTicks` must be a nonnegative integer. Value: `' + num + '`.' );
	}
	debug( 'Current value: %d.', num );

	this._numTicks = num;
	debug( 'New Value: %s.', this._numTicks );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
