'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var Axis = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof Axis, 'function', 'main export is a function' );
	t.end();
});

tape( 'the export is a class constructor', function test( t ) {
	var axis = new Axis({
		'scale': noop,
		'orientation': 'bottom'
	});
	t.equal( axis instanceof Axis, true, 'returns class instance' );
	t.end();
});
