'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:accessor:is-defined' );
var isNull = require( '@stdlib/utils/is-null' );


// IS DEFINED //

/**
* Accessor function which determines where a time series is defined.
*
* @private
* @param {(Array|TypedArray|null)} d - datum
* @returns {boolean} boolean indicating whether a datum is defined
*/
function isDefined( d ) {
	var bool = ( !isNull( d ) && !isNull( d[ 1 ] ) );
	debug( 'Datum: %s. Defined: %s.', JSON.stringify( d ), bool );
	return bool;
} // end FUNCTION isDefined()


// EXPORTS //

module.exports = isDefined;
