'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:accessor:x-value' );


// ACCESSOR //

/**
* x-value accessor.
*
* @private
* @param {NumericArray} d - datum
* @returns {*} x-value
*/
function xValue( d ) {
	var v = d[ 0 ];
	debug( 'Datum: %s. x-value: %s.', JSON.stringify( d ), v.toString() );
    return v;
} // end FUNCTION xValue()


// EXPORTS //

module.exports = xValue;
