'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:accessor:y-value' );


// ACCESSOR //

/**
* y-value accessor.
*
* @private
* @param {NumericArray} d - datum
* @returns {*} y-value
*/
function yValue( d ) {
	var v = d[ 1 ];
	debug( 'Datum: %s. y-value: %d.', JSON.stringify( d ), d );
    return v;
} // end FUNCTION yValue()


// EXPORTS //

module.exports = yValue;
