'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:annotations' );


// SET //

/**
* Sets the plot annotations.
*
* @private
* @param {Array} annotations - plot annotations
* @throws {TypeError} must be an array
*/
function set( annotations ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	// TODO: type check

	debug( 'Current value: %s.', JSON.stringify( this._annotations ) );
	oldVal = this._annotations;

	// TODO: transform to standard format

	this._annotations = annotations;
	debug( 'New value: %s.', JSON.stringify( this._annotations ) );

	this.emit( 'annotationsChanged', this._annotations, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
