'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:avalue' );
var isFunction = require( '@stdlib/utils/is-function' );


// SET //

/**
* Sets the annotation accessor.
*
* @private
* @param {Function} fcn - accessor
* @throws {TypeError} must be a function
*/
function set( fcn ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isFunction( fcn ) ) {
		throw new TypeError( 'invalid value. `aValue` must be a function. Value: `' + fcn + '`.' );
	}
	debug( 'Current value: %s.', this._aValue );
	oldVal = this._aValue;

	this._aValue = fcn;
	debug( 'New Value: %s.', this._aValue );

	this.emit( 'aValueChanged', fcn, oldVal );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
