'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:data' );


// SET //

/**
* Sets the plot data.
*
* @private
* @param {Array} data - plot data
* @throws {TypeError} must be an array
*/
function set( data ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	// TODO: type check

	debug( 'Current value: %s.', JSON.stringify( this._data ) );
	oldVal = this._data;

	// TODO: transform to standard format; maybe a linear array (ndarray; pull from buffer pool?)

	this._data = data;
	debug( 'New Value: %s.', JSON.stringify( this._data ) );

	this.emit( 'dataChanged', this._data, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
