'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:height' );
var isPositiveNumber = require( '@stdlib/utils/is-positive-number' ).isPrimitive;
var events = require( './../events' );


// VARIABLES //

var CHANGE_EVENT = events( 'height' );


// SET //

/**
* Sets the plot height.
*
* @private
* @param {PositiveNumber} height - plot height
* @throws {TypeError} must be a positive number
*/
function set( height ) {
	/*jshint validthis: true */ // TODO: eslint
	if ( !isPositiveNumber( height ) ) {
		throw new TypeError( 'invalid value. `height` must be a positive number. Value: `' + height + '`.' );
	}
	debug( 'Current value: %d.', this._height );

	this._height = height;
	debug( 'New Value: %d.', this._height );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
