'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:is-defined' );
var isFunction = require( '@stdlib/utils/is-function' );


// SET //

/**
* Sets the accessor for defined values.
*
* @private
* @param {Function} fcn - accessor
* @throws {TypeError} must be a function
*/
function set( fcn ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isFunction( fcn ) ) {
		throw new TypeError( 'invalid value. `isDefined` must be a function. Value: `' + fcn + '`.' );
	}
	debug( 'Current value: %s.', this._isDefined );
	oldVal = this._isDefined;

	this._isDefined = fcn;
	debug( 'New Value: %s.', oldVal, this._isDefined );

	this.emit( 'isDefinedChanged', fcn, oldVal );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
