'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:padding-right' );
var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isPrimitive;


// SET //

/**
* Sets the plot right padding.
*
* @private
* @param {NonNegtiveInteger} padding - padding
* @throws {TypeError} must be a nonnegative integer
*/
function set( padding ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isNonNegativeInteger( padding ) ) {
		throw new TypeError( 'invalid value. `paddingRight` must be a nonnegative integer. Value: `' + padding + '`.' );
	}
	debug( 'Current value: %d.', this._paddingRight );
	oldVal = this._paddingRight;

	this._paddingRight = padding;
	debug( 'New value: %d.', this._paddingRight );

	this.emit( 'paddingRightChanged', padding, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
