'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:padding-top' );
var isNonNegativeInteger = require( '@stdlib/utils/is-nonnegative-integer' ).isPrimitive;
var events = require( './../events' );


// VARIABLES //

var CHANGE_EVENT = events( 'paddinTop' );


// SET //

/**
* Sets the plot top padding.
*
* @private
* @param {NonNegtiveInteger} padding - padding
* @throws {TypeError} must be a nonnegative integer
*/
function set( padding ) {
	/*jshint validthis: true */ // TODO: eslint
	if ( !isNonNegativeInteger( padding ) ) {
		throw new TypeError( 'invalid value. `paddingTop` must be a nonnegative integer. Value: `' + padding + '`.' );
	}
	debug( 'Current value: %d.', this._paddingTop );

	this._paddingTop = padding;
	debug( 'New value: %d.', this._paddingTop );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
