'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:title' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// SET //

/**
* Sets the plot title.
*
* @private
* @param {string} str - plot title
* @throws {TypeError} must be a string primitive
*/
function set( str ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isString( str ) ) {
		throw new TypeError( 'invalid value. `title` must be a string primitive. Value: `' + str + '`.' );
	}
	debug( 'Current value: %s.', this._title );
	oldVal = this._title;

	this._title = str;
	debug( 'New value: %s.', this._title );

	this.emit( 'titleChanged', str, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
