'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:width' );
var isPositiveNumber = require( '@stdlib/utils/is-positive-number' ).isPrimitive;


// SET //

/**
* Sets the plot width.
*
* @private
* @param {PositiveNumber} width - plot width
* @throws {TypeError} must be a positive number
*/
function set( width ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isPositiveNumber( width ) ) {
		throw new TypeError( 'invalid value. `width` must be a positive number. Value: `' + width + '`.' );
	}
	debug( 'Current value: %d.', this._width );
	oldVal = this._width;

	this._width = width;
	debug( 'New value: %d.', this._width );

	this.emit( 'widthChanged', width, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
