'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xaxisorient' );


// VARIABLES //

var ORIENTATIONS = {
	'bottom': true,
	'top': true
};


// SET //

/**
* Sets the x-axis orientation.
*
* @private
* @param {string} orientation - axis orientation
* @throws {TypeError} must be either `'bottom'` or `'top'`
*/
function set( orientation ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( ORIENTATIONS[ orientation ] === void 0 ) {
		throw new TypeError( 'invalid value. `xAxisOrient` must be either `bottom` or `top`. Value: `' + orientation + '`.' );
	}
	debug( 'Current value: %s.', this._xAxisOrient );
	oldVal = this._xAxisOrient;

	this._xAxisOrient = orientation;
	debug( 'New value: %s.', this._xAxisOrient );

	this.emit( 'xAxisOrientChanged', orientation, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
