'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xmax' );
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isNull = require( '@stdlib/utils/is-null' );


// SET //

/**
* Sets the maximum value of the x-axis domain.
*
* @private
* @param {(FiniteNumber|null)} min - maximum value
* @throws {TypeError} must be a finite number primitive or null
*/
function set( max ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	// FIXME: check for finite number primitive
	if ( !isNumber( max ) && !isNull( max ) ) {
		throw new TypeError( 'invalid value. `xMax` must be a finite number primitive or null. Value: `' + max + '`.' );
	}
	debug( 'Current value: %s.', this._xMax.toString() );
	oldVal = this._xMax;

	this._xMax = max;
	debug( 'New value: %s.', this._xMax.toString() );

	this.emit( 'xMaxChanged', max, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
