'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xtickformat' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// SET //

/**
* Sets the x-axis tick format.
*
* @private
* @param {string} fmt - axis tick format
* @throws {TypeError} must be a string primitive
*/
function set( fmt ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isString( fmt ) ) {
		throw new TypeError( 'invalid value. `xTickFormat` must be a string primitive. Value: `' + fmt + '`.' );
	}
	debug( 'Current value: %s.', this._xTickFormat );
	oldVal = this._xTickFormat;

	this._xTickFormat = fmt;
	debug( 'New value: %s.', this._xTickFormat );

	this.emit( 'xTickFormatChanged', fmt, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
