'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xvalue' );
var isFunction = require( '@stdlib/utils/is-function' );


// SET //

/**
* Sets the x-value accessor.
*
* @private
* @param {Function} fcn - accessor
* @throws {TypeError} must be a function
*/
function set( fcn ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( !isFunction( fcn ) ) {
		throw new TypeError( 'invalid value. `xValue` must be a function. Value: `' + fcn + '`.' );
	}
	debug( 'Current value: %s.', this._xValue );
	oldVal = this._xValue;

	this._xValue = fcn;
	debug( 'New value: %s.', this._xValue );

	this.emit( 'xValueChanged', fcn, oldVal );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
