'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:yaxisorient' );


// VARIABLES //

var ORIENTATIONS = {
	'left': true,
	'right': true
};


// SET //

/**
* Sets the y-axis orientation.
*
* @private
* @param {string} orientation - axis orientation
* @throws {TypeError} must be either `'left'` or `'right'`
*/
function set( orientation ) {
	/*jshint validthis: true */ // TODO: eslint
	var oldVal;

	if ( ORIENTATIONS[ orientation ] === void 0 ) {
		throw new TypeError( 'invalid value. `yAxisOrient` must be either `left` or `right`. Value: `' + orientation + '`.' );
	}
	debug( 'Current value: %s.', this._yAxisOrient );
	oldVal = this._yAxisOrient;

	this._yAxisOrient = orientation;
	debug( 'New value: %s.', this._yAxisOrient );

	this.emit( 'yAxisOrientChanged', orientation, oldVal );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
